import React, { Component } from 'react'

export default class Cover extends Component {
  // constructor () {
  //   super() 
  //  this.state = {
  //   TheWord: [ ] ,
  //   TheMeaning: [ ] 
  //  }
  //  this.cliksub = this.cliksub.bind(this)
  // }
  clicksub() {
    let word = document.getElementById("npt-word").value
    let meaning = document.getElementById("npt-meaning").value
    this.state.TheWord.push(word)
    this.state.TheMeaning.push(meaning)
    console.log(this.state.TheMeaning)
    console.log(this.state.TheWord)
  }
  clickplay() {

  }
  render() {  
      return (
        <div className='form-input'>
          <form>
           <div className='npt'>

            <h6> The Word </h6>
          <input id="npt-word" type="text" />

          <h6> The meaning </h6>
            <input id="npt-meaning" type="text" />

          </div> 

          <div className='btn'>

            <div className='btn-sub'  onClick={this.clicksub}>submit</div>

            <div className='btn-play' onClick={this.clickplay}>play</div>

          </div> 
          </form> 
        </div>
      )

  }
}
