import React, { useState } from 'react'
import './Cover.css'
import { Link } from 'react-router-dom'
import { useSelector , useDispatch } from 'react-redux'
import { nanoid } from '@reduxjs/toolkit'
import { wordAdded } from '../../redux/meanSlice'
const Cover = (props) => {
  let [ word , setword ] = useState("")
  let [ mean , setmean ] = useState("")

  let dispatch = useDispatch

  const onSaveMeanClicked = () => {
    if ( word && mean ) {
      dispatch(
        meanAdded({
          id: nanoid(),
          word, 
          mean
        })
      )
    }
  }


    return (
   <section>
      <h1 className="header"> Hello There, Welcome to our Flash Card App</h1>
        <form>
          <div className='the'>
          <label>The Word</label>
          <input
              id="npt-meaning"
              type="text"
              onChange={(e) => {setword(e.target.value)}}
              value={word}
          />
          </div>
          <div className='the'>
          <label>The meaning</label>
          <input
              id="npt-meaning"
              type="text"
              onChange={(e) => {setmean(e.target.value)}}
              value={mean}
          />
          </div>

          <div className='button'>
              <button type='button' onclick={onSaveMeanClicked} className='botton'>submit</button>
              <Link className='Link' to="/play">play</Link>
          </div>

      </form> 
        {word}
   </section>
    )


}

export default Cover
