import './App.css';

import Cover from './Container/Cover/Cover';
import { useState } from 'react';
function App() {
  let [ thewords , setheword ] = useState([])
  let addthewords = (theword) => {
     setheword([theword , ...thewords  ])
  }
  return (
    <div className="App">

   <Cover onSubmit={addthewords}/>

   { 
   thewords.map((word) => (
    <TheCard word={word} />
   ))
   }

    </div>
  );
}

export default App;
