import React, { useState } from 'react'
import shortid from 'shortid'
import './Cover.css'
const Cover = (props) => {
  let [ word , setword ] = useState("")
  let handlevent = (e) => { 
    e.preventDefault()
    props.onSubmit({
      id: shortid.generate(),
      word
    })
  }
    return (
      <div className='form-input'>
        <form onSubmit={handlevent} >
         <div className='npt'>

          <h6> The Word </h6>
        <input id="npt-word" type="text"  />

        <h6> The meaning </h6> 
          <input
            id="npt-meaning"
            type="text"
            onChange={(e) => {setword(e.target.value)}}
            value={word}
              />

        </div>  

        <div className='btn'>

          <div className='btn-sub' onClick={handlevent}>submit</div>

          <div className='btn-play' >play</div>

        </div> 
        </form> 
      </div>
    )


}

export default Cover
