   <section>
      <h1 className="header"> Hello There, Welcome to our Flash Card App</h1>
        <form>
          <div className='the'>
          <label>The Word</label>
          <input
              id="npt-meaning"
              type="text"
              onChange={(e) => {setword(e.target.value)}}
              value={word}
          />
          </div>
          <div className='the'>
          <label>The meaning</label>
          <input
              id="npt-meaning"
              type="text"
              onChange={(e) => {setmean(e.target.value)}}
              value={mean}
          />
          </div>


              <button 
              type='button'
              onClick={onSaveMeanClicked}
              className='button'
              >submit</button>


              <Link  className='button' to="/play">play</Link>

              <button type="button" onClick={handleNext} className="button">
